<?php class cleanto_staff_commision{			public $id;	public $order_id;	public $staff_id;	public $amt_payable;	public $advance_paid;	public $net_total;	public $payment_method;	public $transaction_id;	public $payment_date;	public $table_name="ct_staff_commision";	public $conn;	public function readall_booking()	{		$query = "select `service_id`,`client_id`,`staff_ids`,`order_id`, `booking_status`, `booking_date_time` from `ct_bookings` where `staff_ids`<>'' group by `service_id`,`client_id`,`staff_ids`,`order_id`, `booking_status`, `booking_date_time` ";		$result=mysqli_query($this->conn,$query);		return $result;	}	public function get_staff_bookingandpayment_by_date($start, $end)	{		$query = "select `service_id`,`client_id`,`staff_ids`,`order_id`, `booking_status`, `booking_date_time` from `ct_bookings` where `staff_ids`<>'' and `booking_date_time` between '".$start."' and '".$end."' group by  `service_id`,`client_id`,`staff_ids`,`order_id`, `booking_status`, `booking_date_time`";		$result=mysqli_query($this->conn,$query);		return $result;	}	public function get_staff_bookingandpayment_by_dateser($start, $end, $sid)	{		$query = "select `service_id`,`client_id`,`staff_ids`,`order_id`, `booking_status`, `booking_date_time` from `ct_bookings` where `staff_ids`<>'' and `service_id` = '".$sid."' and `booking_date_time` between '".$start."' and '".$end."' group by `service_id`,`client_id`,`staff_ids`,`order_id`, `booking_status`, `booking_date_time`";		$result=mysqli_query($this->conn,$query);		return $result;	}	public function get_service_name($id)	{		$query = "select * from `ct_services` where `id`=".$id;		$result = mysqli_query($this->conn,$query);		$value = mysqli_fetch_array($result);		return $value['title'];	}	public function get_client_name($id)	{		$query = "select * from `ct_users` where `id`=".$id;		$result = mysqli_query($this->conn,$query);		$value = mysqli_fetch_array($result);		return $value['first_name'].' '.$value['last_name'];	}	public function get_staff_name($id)	{		$query = "select * from `ct_admin_info` where `id` IN (".$id.")";		$result = mysqli_query($this->conn,$query);		$staff_name = '';		$total = mysqli_num_rows($result);		if($total == 1){			$value = mysqli_fetch_array($result);			$staff_name = $value['fullname'];		}else if($total > 1){			while($value = mysqli_fetch_array($result)){				$staff_name .= $value['fullname'].', ';			}		}		return $staff_name;	}	public function get_staff_detail($id)	{		$query = "select * from `ct_admin_info` where `id` IN (".$id.")";		$result = mysqli_query($this->conn,$query);		return $result;	}	public function get_net_total($id)	{		$query = "select * from `ct_payments` where `order_id`=".$id;		$result = mysqli_query($this->conn,$query);		$value = mysqli_fetch_array($result);		return $value['net_amount'];	}		public function insert_staff_commision($order_id,$staff_id,$amt_payable,$advance_paid,$net_total,$payment_method,$transaction_id,$payment_date)	{		$query = "INSERT INTO `ct_staff_commission` (`id`, `order_id`, `staff_id`, `amt_payable`, `advance_paid`, `net_total`, `payment_method`, `transaction_id`, `payment_date`) VALUES (NULL, '".$order_id."', '".$staff_id."', '".$amt_payable."', '".$advance_paid."', '".$net_total."', '".$payment_method."', '".$transaction_id."', '".$payment_date."');";		$result = mysqli_query($this->conn,$query);		return $result;	}		public function readall_ct_staff_commision()	{		$query="select * from `ct_staff_commission`";		$result=mysqli_query($this->conn,$query);		return $result;	}		public function check_staff_commision_payment($order_id,$staff_id){		$query = "select * from `ct_staff_commission` where `order_id`='".$order_id."' and `staff_id`='".$staff_id."'";		$result = mysqli_query($this->conn,$query);		$value = mysqli_num_rows($result);		return $value;	}		public function update_staff_commision_payment($order_id,$staff_id,$amt_payable,$advance_paid,$net_total,$payment_date){		$query = "update `ct_staff_commission` set `amt_payable`='".$amt_payable."', `advance_paid` ='".$advance_paid."', `net_total` ='".$net_total."', `payment_date`  ='".$payment_date."' where `order_id`='".$order_id."' and `staff_id`='".$staff_id."'";		$result = mysqli_query($this->conn,$query);		$value = mysqli_num_rows($result);		return $value;	}		public function staff_service_details($staff_id){		$arr = array();		$query = "select `order_id`, `service_id`, `booking_date_time`, `staff_ids` from `ct_bookings` group by `order_id`, `service_id`, `booking_date_time`, `staff_ids`";				$result=mysqli_query($this->conn,$query);		while($value = mysqli_fetch_array($result)){			$stafid = explode(',',$value['staff_ids']);			if(sizeof($stafid) > 0){				foreach($stafid as $sid){					if($sid == $staff_id){						array_push($arr,$value);					}				}			}		}		return $arr;	}		public function readall_staff_pymt_ser(){		$query = "select * from `ct_services`";		$result = mysqli_query($this->conn,$query);		return $result;	}		public function get_booking_nettotal($staff_id, $order_id)	{		$query = "select sum(`net_total`) from `ct_staff_commission` where `staff_id`='".$staff_id."' and `order_id`='".$order_id."'";		$result=mysqli_query($this->conn,$query);		$value = mysqli_fetch_array($result);		if(isset($value) && sizeof($value) > 0){			return $value[0];		}else{			return 0;		}	}		public function get_payment_staff_by_date($start, $end)	{		$query = "select * from `ct_staff_commission` where `payment_date` between '".$start."' and '".$end."'";		$result=mysqli_query($this->conn,$query);		return $result;	}	public function get_booking_assign($staff_id){		$query = "select * from ct_staff_commission where staff_id='".$staff_id."'";		$result=mysqli_query($this->conn,$query);		return $result;	}	}?>